import {connect} from 'react-redux'
import './App.css';

class App extends Component {


  handleSubmit = e => {
    e.preventDefault()
    this.props.getMovies(this.props.text)
  }
  handleChange = e => {
    this.props.searchMovie(e.target.value)
  }
  render(){
    return (
      <div className="App"> 
       { 
        this.props.movies.map((id, otherprops) => (
        <MovieList key={id} handleEvent={this.handleEvent} handleChange={this.hnadChange} {...otherprops} />
        ))

        }

      </div>
    );
  }
}
const mapStateToProps = state =>({
  text: state.search.text,
  movies:state.search.movies
})
export default connect(mapStateToProps, {getMovies, searchMovie})(App);


