import axios from 'axios'
const apikey = 'e1d0ed2f09aee761ddab2e211181612e'

export const getMovies = () =>  dispatch => {
    dispatch({ type: 'GET_LIST_STARTED' })
        axios.create({
            baseURL: `https://api.themoviedb.org/3/search/movie?api_key=${apikey}&query=${this.state.searchterm}`
          })
        .then(d=> d.json())
        .then(d => {
          dispatch({type: 'GET_LIST_ITEMS_SUCCESS', movies:[...d.results]})
        })
}
