import axios from 'axios'
const apikey = 'e1d0ed2f09aee761ddab2e211181612e'

export const getMovies = () => {
    return ((dispatch, getState) =>{
        axios.create({
            baseURL: `https://api.themoviedb.org/3/search/movie?api_key=${apikey}&query=${this.state.searchterm}`
          })
        .then(d=> d.json())
        .then(d => {
          dispatch({ movies:[...d.results]})
        })
    })
}
