import React, { Component } from 'react';
 import MovieList from './components/MovieList'
import {searchMovie, getMovies} from './actions'
import {connect} from 'react-redux'
import './App.css';

class App extends Component {

  handleSubmit = e => {
    e.preventDefault()
    this.props.getMovies(this.props.text)
  }
  handleChange = e => {
    this.props.searchMovie(e.target.value)
  }
  render(){
    return (
      <div className="App"> 
       { (movies) ? 
                movies.map(m => (
                    <div key={m.id}>
                    {m.title}
                    <img src={`https://image.tmdb.org/t/p/w500${m.poster_path}`}  alt="" />
                    </div>
                ))
                : null
                }
        <MovieList handleEvent={this.handleEvent} handleChange={this.hnadChange} />
      </div>
    );
  }
}
const mapStateToProps = state =>({
  text: state.search.text,
  movies:state.search.movies
})
export default connect(mapStateToProps, {getMovies, searchMovie})(App);
