import React, { useState } from 'react';
import {Tooltip, Col, Row} from "reactstrap";
const SingleMovie = ({poster_path, title, overview, original_title}) => {
    const [tooltipOpen, setTooltipOpen] = useState(false);

    const toggle = () => setTooltipOpen(!tooltipOpen);

    return (
        <Col id="DisabledAutoHideExample" xs="3">
            <div className='movie_card' >
            <h5 className='search_result_card_title'>{title}</h5>
            <img className='search_image img-fluid' src={`https://image.tmdb.org/t/p/w500${poster_path}`}  alt="" />
            </div>

            <Tooltip className='card_tooltip' placement="top" isOpen={tooltipOpen} autohide={false} target="DisabledAutoHideExample" toggle={toggle}>
              <h3>{original_title}</h3>
              <p>{overview}</p>
            </Tooltip>
        </Col>
    )
}
export default  SingleMovie
