import axios from 'axios'




const apikey = 'e1d0ed2f09aee761ddab2e211181612e'

export const getMovies = () => {
    return ((dispatch, getState) =>{
        axios.create({
            baseURL: `https://api.themoviedb.org/3/search/movie?api_key=${apikey}&query=${this.state.searchterm}`
          })
        .then(d=> d.json())
        .then(d => {
          dispatch({ movies:[...d.results]})
        })
    })
}

export const getAllList = () => {
    return (dispatch, getState) => {
        dispatch({ type: 'GET_LIST_STARTED' })
        fetch('http://5b35ede16005b00014c5dc86.mockapi.io/list')
		  .then((response) => {
		    return response.json();
		  })
		  .then((myJson) => {
		    dispatch({ type: 'GET_LIST_ITEMS_SUCCESS', lists: myJson })
		  });
    }
}
