import React, { Component } from "react";
import { connect } from "react-redux";
import {fetchMovie} from '../actions'
import { Card, CardText, Row, Col} from 'reactstrap';
class MovieDetails extends Component {
    componentDidMount(){
        this.props.fetchMovie(this.props.match.params.id);
    }
    render() {
        const {title, poster_path, overview, tagline ,release_date,vote_average,vote_count, production_companies, production_countries} = this.props.movies
        return (
            <div className='container'>

                <Card body>
                    <h2 className="search_result_card_title">{title}</h2>

                    <Row xs="2" >
                        <Col> <span><img
                className="search_image img-fluid"
                src={`https://image.tmdb.org/t/p/w500${poster_path}`}
                alt=""
                /></span>
                        </Col>
                        <Col>
                        <CardText><h5>{tagline}</h5></CardText>
                        <CardText>{overview}</CardText> 
                        <CardText><h6>release  date</h6> {release_date}</CardText> 
                        <CardText><h6>vote average</h6> {vote_average}</CardText>
                        <CardText><h6>vote count</h6>  {vote_count}</CardText>
                        <div>
                            <h6>Production Companies</h6>
                            { production_companies && production_companies.map(m=>(<div key={m.name}>{ m.name}</div>
                        ))}
                        </div>
                        <div>
                            <h6>Production Countries</h6>
                            { production_countries && production_countries.map(m=>(<div key={m.name}>{ m.name}</div>
                        ))}
                        </div>
                        </Col>
                    </Row>


                </Card>
            </div>
        );
    };
}
const mapStateToProps = state => ({
  movies: state.trending.movieDetails
});
export default connect(mapStateToProps, { fetchMovie})(MovieDetails);
