import React, { Component } from 'react';
/// import MovieList from './components/MovieList'
import {searchMovie, getMovies} from './actions'
import {connect} from 'react-redux'
import './App.css';

class App extends Component {

  handleSubmit = e => {
    e.preventDefault()
    this.props.getMovies(this.props.text)
  }
  handleChange = e => {
    this.props.searchMovie(e.target.value)
  }
  render(){
    return (
      <div className="App">
        {/* <MovieList handleEvent={this.handleEvent} handleChange={this.hnadChange} /> */}
        <form onSubmit={this.handleSubmit}>
               <input onChange={this.handleChange} />
        </form>
        <div>
          { (this.props.movies) ? 
          this.props.movies.map(m => (
            <div key={m.id}>
              {m.title}
            </div>
          ))
          : null
          }
        </div>
      </div>
    );
  }
}
const mapStateToProps = state =>({
  text: state.search.text,
  movies:state.search.movies
})
export default connect(mapStateToProps, {getMovies, searchMovie})(App);
