import React, { Component } from 'react';
import MovieList from './components/SongList'
import './App.css';

class App extends Component {
  constructor(props){
    super(props)
    this.state={
      movies:[],
      searchterm:''
    }
    this.apiKey = process.env.REACT_APP_API
  }
  handleEvent = (e) => {
    e.preventDefault()
    fetch(`https://api.themoviedb.org/3/search/movie?api_key=${this.apiKey}&query=${this.state.REACT_APP_API}`)
    .then(d=> d.json())
    .then(d => {
      this.setState({ movies:[...d]})
    })
  }
  render(){
    return (
      <div className="App">
        <MovieList />
      </div>
    );
  }
}

export default App;
