const initstate = {
    text : '',
    movies: [],
    loader:false,
    movie:[]
}

export default (state = initstate, action) => {
    switch(action.type){
        case "SEARCH_MOVIE" :
            return {
                ...state,
                text:action.payload,
                loader:false
            }
        case "GET_MOVIE":
            return{
                ...state,
                movies:action.payload
            } 
        default: return state    
    }
}
