import React, { Component } from "react";
import { connect } from "react-redux";
import {fetchPerson} from '../actions'
import { Card, ROw} from 'reactstrap';
import './MovieDetails.scss'
class ActorDetail extends Component {
    componentDidMount(){
        this.props.fetchPerson(this.props.match.params.imdb_id);
    }
    render() {
        const {title} = this.props.actor
        return (
            <div className='container'>
                <Card className='movie_details' body>
                    <h2 className="movie_card_title">{title}</h2>         
                    <Row xs="2" >
                    </Row>
                </Card>
            </div>
        );
    };
}
const mapStateToProps = state => ({
  actor: state.search.actor
});
export default connect(mapStateToProps, {fetchPerson})(ActorDetail);
