import React from "react";
import { Tooltip } from "reactstrap";

const SingleMovie = ({ poster_path, title, id, overview, original_title }) => {
  const [tooltipOpen, setTooltipOpen] = React.useState(false);
  const toggle = () => setTooltipOpen(!tooltipOpen);
  return (
    <>
      <div className="movie_card" id={"Tooltip-" + id}>
        <h5 className="search_result_card_title">{title}</h5>
        <img
          className="search_image img-fluid"
          src={`https://image.tmdb.org/t/p/w500${poster_path}`}
          alt=""
        />
      </div>
      <Tooltip
        placement="auto"
        isOpen={tooltipOpen}
        target={"Tooltip-" + id}
        toggle={toggle}
      >
      <p>{original_title}</p>
       <p style={{fontSize:'10px'}}>{overview}</p> 

      </Tooltip>
    </>
  );
};
export default SingleMovie;
