import axios from 'axios'
const apikey = 'e1d0ed2f09aee761ddab2e211181612e'

export const searchMovie = text => dispatch => {
    dispatch({
        type: "SEARCH_MOVIE",
        payload: text
    })
}
export const getMovies = (text) =>  dispatch => {
    axios.get(`https://api.themoviedb.org/3/search/movie?api_key={apikey}&query={text}`)
    .then(d => {
      dispatch({type: 'GET_LIST_ITEMS_SUCCESS', payload:d.results})
      console.log(d.data)
    })
}
