import React, { Component } from 'react';
import MovieList from '../../src/components/MovieList'
import {searchMovie, getMovies} from '../../src/actions'
import {connect} from 'react-redux'

class Home extends Component {


 handleSubmit = e => {
   e.preventDefault()
   this.props.getMovies(this.props.text)
 }
 handleChange = e => {
   this.props.searchMovie(e.target.value)
 }
 render(){
   return (
     <div className="App"> 
      { 
       this.props.movies.map(({id, ...otherprops}) => (
       <MovieList key={id} handleSubmit={this.handleSubmit} handleChange={this.handleChange} {...otherprops} />
       ))
       }
     </div>
   );
 }
}
const mapStateToProps = state =>({
 text: state.search.text,
 movies:state.search.movies
})
export default connect(mapStateToProps, {getMovies, searchMovie})(Home);
