const initstate = {
    trendingmovies: [],
    movieDetails:[],
    actorDetails:[]
}
export default (state = initstate, action) => {
    switch(action.type){
        case "TRENDING_MOVIE":
            return{
                ...state,
                trendingmovies:action.payload
            } 
        case "FETCH_MOVIE":
            return{
                ...state,
                movieDetails:action.payload
        } 
        case "FETCH_ACTOR":
        return {
          ...state,
          actor: action.payload
        };
        default: return state    
    }
}
