import React, { Component } from 'react';
import {searchMovie, getMovies} from '../../src/actions'
import {connect} from 'react-redux'
import {Form, Input, Row, Col, Tooltip} from 'reactstrap';
import { useState } from 'react';
import './MovieList.scss'

class MovieList extends Component {
 handleSubmit = e => {
   e.preventDefault()
   this.props.getMovies(this.props.text)
 }
 handleChange = e => {
   this.props.searchMovie(e.target.value)
 }
 render(props){
   return (
     <div className="search_component"> 
        <Form className='container-fluid' onSubmit={this.handleSubmit}>
            <Input onChange={this.handleChange}  placeholder='search by movie name' />
            <Row  className='search_result'>
                { 
                    this.props.movies.map(m => (
                        <Col  xs="2" key={m.id} id="UncontrolledTooltipExample">

                        <div className='movie_card'>
                            <h5 className='search_result_card_title'>{m.title}</h5>
                        <img className='search_image img-fluid' src={`https://image.tmdb.org/t/p/w500${m.poster_path}`}  alt="" />
                        </div>
                        <UncontrolledTooltip placement="right" target="UncontrolledTooltipExample">
                            Hello world!
                        </UncontrolledTooltip>
                        </Col>
                    ))
                }
            </Row>
        </Form>
     </div>
   );
 }
}
const mapStateToProps = state =>({
 text: state.search.text,
 movies:state.search.movies
})
export default connect(mapStateToProps, {getMovies, searchMovie})(MovieList);
