import React, { Component } from 'react';
import MovieList from './components/MovieList'
import './App.css';

class App extends Component {

  constructor(props){
    super(props)
    this.state={
      movies:[],
      searchterm:''
    }

  }

  handleEvent = (e) => {
    const apikey = 'e1d0ed2f09aee761ddab2e211181612e'
    e.preventDefault()
    fetch(`https://api.themoviedb.org/3/search/movie?api_key=${apikey}&query=${this.state.searchterm}`)
    .then(d=> d.json())
    .then(d => {
      console.log(d.results)
      //this.setState({ movies:[...d.results]})
    })
  }
  hnadChange = (e) => {
    this.setState({ searchterm: e.target.value})
  }
  render(){
    return (
      <div className="App">
        <MovieList handleEvent={this.handleEvent} handleChange={this.hnadChange} />
      </div>
    );
  }
}

export default App;
