import axios from 'axios'
const apikey = 'e1d0ed2f09aee761ddab2e211181612e'

export const searchMovie = text => dispatch => {
    dispatch({
        type: "SEARCH_MOVIE",
        payload: text
    })
}
export const getMovies = text =>  dispatch => {
    axios.get(`https://api.themoviedb.org/3/search/movie?api_key=${apikey}&query=${text}`)
    .then(d => {dispatch({type: 'GET_MOVIE', payload:d.data.results
})
      console.log(d.data)
    })
}

export const trendingMovies = () =>  dispatch => {
    axios.get(`https://api.themoviedb.org/3/trending/all/day?api_key=${apikey}`)
    .then(d => {dispatch({type: 'TRENDING_MOVIE', payload:d.data
})
      console.log(d.data)
    })
}
