import {Tooltip, Col} from "reactstrap";
const SingleMovie = ({poster_path, title, id}) => {
    const [tooltipOpen, setTooltipOpen] = useState(false);

    const toggle = () => setTooltipOpen(!tooltipOpen);

    return (
        <Col  xs="2" id="DisabledAutoHideExample">
            <div className='movie_card' >
            <h5 className='search_result_card_title'>{title}</h5>
            <img className='search_image img-fluid' src={`https://image.tmdb.org/t/p/w500${poster_path}`}  alt="" />
            </div>
            <Tooltip placement="top" isOpen={tooltipOpen} autohide={false} target="DisabledAutoHideExample" toggle={toggle}>
              <span>{overview}</span>
            </Tooltip>

        </Col>
    )
}
export default  SingleMovie
