import React, { Component } from 'react';
/// import MovieList from './components/MovieList'
import {searchMovie, getMovies} from './actions'
import {connect} from 'react-redux'
import './App.css';

class App extends Component {


  // constructor(props){
  //   super(props)

  // }
  handleSubmit = e => {
    e.preventDefault()
    this.props.getMovies(this.props.text)
  }
  handleChange = e => {
    this.props.searchMovie({ searchterm: e.target.value})
  }
  render(){
    console.log(this.props)
    return (
      <div className="App">
        {/* <MovieList handleEvent={this.handleEvent} handleChange={this.hnadChange} /> */}
        <form onSubmit={this.handleSubmit}>
               <input onChange={this.handleChange} />
        </form>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {text :state.search.text}
}
export default connect(mapStateToProps, {getMovies, searchMovie})(App);
