import React, { useState }  from 'react'
import { Tooltip } from 'reactstrap'
import {Link} from 'react-router-dom'
import './Single.scss'
const SingleMovie = ({ poster_path, title, id, overview, original_title }) => {
  const [tooltipOpen, setTooltipOpen] = useState(false);
  const toggle = () => setTooltipOpen(!tooltipOpen);
  return (
    <>
    <Link className="movie_card" to={/movie/ + id}>
      <div  id={"Tooltip-" + id}>
        <h5 className="movie_card_title">{title}</h5>
        <img
          className="search_image img-fluid"
          src={`https://image.tmdb.org/t/p/w500${poster_path}`}
          alt=""
        />
      </div>
      <Tooltip id='tooltip_custom' 
        placement="auto"
        isOpen={tooltipOpen}
        target={"Tooltip-" + id}
        toggle={toggle}
      >
        <div>
          <h3>{original_title}</h3>
        </div>
       <p>{original_title}</p>
       <p style={{fontSize:'10px'}}>{overview}</p> 
      </Tooltip>
    </Link>

    </>
  );
};
export default SingleMovie;
