import React, { useState } from "react";
import { Button, Tooltip } from "reactstrap";
const SingleMovie = ({poster_path, title}) => {
    const { item, id } = props;
  const [tooltipOpen, setTooltipOpen] = useState(false);

  const toggle = () => setTooltipOpen(!tooltipOpen);
    return (
        <Col  xs="2"  href="#" id="UncontrolledTooltipExample">
            <div className='movie_card'>
            <h5 className='search_result_card_title'>{title}</h5>
            <img className='search_image img-fluid' src={`https://image.tmdb.org/t/p/w500${poster_path}`}  alt="" />
            </div>
            <UncontrolledTooltip placement="right" target="UncontrolledTooltipExample">
            Hello world!
            </UncontrolledTooltip>
        </Col>
    )
}
export default  SingleMovie
