import React, { Component } from "react";
import { connect } from "react-redux";
import {fetchPerson} from '../actions'
import { Card, CardText, Row, Col} from 'reactstrap';
import './MovieDetails.scss'
class ActorDetail extends Component {
    componentDidMount(){
        this.props.fetchPerson(this.props.match.params.id);
    }
    render() {
        const {title, poster_path, tagline ,genres} = this.props.actor
        return (
            <div className='container'>
                <Card className='movie_details' body>
                    <h2 className="movie_card_title">{title}</h2>         
                    <Row xs="2" >
                        <Col>
                            <span>
                                <img
                                className="search_image img-fluid"
                                src={`https://image.tmdb.org/t/p/w500${poster_path}`}
                                alt=""
                                />
                            </span>
                        </Col>
                        <Col>
                        <CardText><h5>{tagline}</h5></CardText>
                        </Col>
                    </Row>
                </Card>
            </div>
        );
    };
}
const mapStateToProps = state => ({
  actor: state.search.actor
});
export default connect(mapStateToProps, {fetchPerson})(ActorDetail);
